import React, { useContext } from 'react'
import "./UserOrder.css"
import { DataStore } from '../../../App';

const slides = document.querySelectorAll(".userslide");

// loop through slides and set each slides translateX
slides.forEach((slide, indx) => {
  slide.style.transform = `translateX(${indx * 100}%)`;
});

// current slide counter
let curSlide = 0;
// maximum number of slides
let maxSlide = slides.length - 1;
console.log(slides)

// add event listener and navigation functionality
const btnNext = () => {
  // check if current slide is the last and reset current slide
  if (curSlide === maxSlide) {
    curSlide = 0;
  } else {
    curSlide++;
  }

  //   move slide by -100%
  slides.forEach((slide, indx) => {
    slide.style.transform = `translateX(${100 * (indx - curSlide)}%)`;
  });
};

// select next slide button
const prevSlide = () => {
  // check if current slide is the first and reset current slide to last
  if (curSlide === 0) {
    curSlide = maxSlide;
  } else {
    curSlide--;
  }
  //   move slide by 100%
  slides.forEach((slide, indx) => {
    slide.style.transform = `translateX(${100 * (indx - curSlide)}%)`;
  });
};

const UserOrder = () => {
   const ctx = useContext(DataStore)
  return (
    <div className="slider myCssOnSlider">

  {ctx.userOrder.map((value) => {
    return (
      <div key={value.id} className="userslide">
    <div className='userInfo'>
    <div>{value.name}</div>
    <div>{value.email}</div>
    <div>{value.phone}</div>
    <div>{value.add}</div>
    <div>{value.cPin}</div>
    </div>
    <ul>
    <div className='order-list'>
      {value.food.map((foodvalue) => {
        return (
          <li className='meal'>
          <h3>{foodvalue.name}</h3>
          <div className='price' > {foodvalue.count}</div>
          <div className='price' >{foodvalue.count * foodvalue}</div>
      </li>
        )
      })}

      <li className='meal'>
          <div>Total</div>
          <div>example money</div>
      </li>
    </div>
    </ul>

  </div>
    )

  })}

  <button onClick={btnNext} className="btnSlider btn-next"> {">"} </button>
  <button onClick={prevSlide} className="btnSlider btn-prev">
    {'<'} </button>
</div>
  )
}

export default UserOrder

