function CardValue(props) {
      const ctx = useContext(DataStore)
    const Total = ctx.myOrder >= 0 ? [0] : (ctx.myOrder.map(a => a.price))
    const onsubtract = (event) => {
       ctx.onsetMyOrder({
        id : event.target.id ,
        true : true,
        min : true
        })
        }
    const onAdding = (event) => {
        ctx.onsetMyOrder({
        id : event.target.id ,
        true : true,
        plus : true
        })
        }
  return (
   <div className='popup card-value'>
                <ul>
             {ctx.myOrder.length === 0
                ? <center className='m-5'><b>Nothimg to Show</b></center>
                :( ctx.myOrder.map((avaMeal) => {
             return (
            <li key={avaMeal.id} className='meal'>
               <div >
                    <h3>{avaMeal.name}</h3>
                    <div className='price'>{`â‚¹ ${avaMeal.price.toFixed(2)}`}</div>
               </div>
                    <div className='price'>
                        <button onClick={onsubtract} id={avaMeal.id} className='btn btn-success m-1' > - </button>
                        <b>x{avaMeal.count}</b>
                        <button onClick={onAdding} id={avaMeal.id} className='btn btn-success m-1' > + </button>
                    </div>
            </li>
            );
            }))}
                <li className='meal'>
                 <h3>Total</h3>
                 <div className='price'>{`â‚¹ ${Total.reduce((a,b) => a+b).toFixed(2)}`}</div>
                </li>

            <li className='meal'>
                 <button onClick={() => ctx.onCloseCard(false)} className='btn btn-success mx-5 px-5'> Cancel</button>
                 <button className='btn btn-success mx-5 px-5'> Order ðŸ˜‹</button>
            </li>
        </ul>
      </div>
  )
}

