const useHttpFood = () => {
    const sendData = async (url,method = 'GET', newData, headers ) => {

        const res = await fetch(url, {
                method : method,
                body : JSON.stringify(newData),
                headers : {
                    "Content-Type": "application/json",
                }
            })
            await res.json();}
    return [sendData];
}

export default useHttpFood
