const useHttpFood = () => {
    const sendData = async (newData) => {

        const res = await fetch(`https://aku-310-default-rtdb.firebaseio.com/order/${newData.food.id}.json`, {
                method : 'POST',
                body : JSON.stringify(newData),
                headers : {
                    "Content-Type": "application/json",
                }
            })
            await res.json();}
    return ;
}

export default useHttpFood
