const useHttpFood = () => {
    const sendData = async (url,method = 'GET', newData, headers = {}) => {

        const res = await fetch(url, {
                method : method,
                body : JSON.stringify(newData),
                headers : headers
            })
            await res.json();}
    return [sendData];
}

export default useHttpFood
