const useHttpFood = () => {
    const sendData = async (url,method,newData,) => {

        const res = await fetch(url, {
                method : 'POST',
                body : JSON.stringify(newData),
                headers : {
                    "Content-Type": "application/json",
                }
            })
            await res.json();}
    return [sendData];
}

export default useHttpFood
