import React , {useEffect, useState} from 'react'
import MealsIteam from './Title/MealsIteam'
import FloatMessage from './Title/FloatMessage'
import CardValue from "../Body/CardValue"
function AppBody(props) {
    const [myOrder , setMyOrder] = useState([]);  
    const onsetMyOrder = (newOrder) => {
       myOrder.map((check , i ) => {
        if(check.id === newOrder.id && newOrder['true']){
          if(check.count === 1){
               myOrder.splice(i,1)
          }

          if(check.count > 0 && newOrder['min']){
              check.count += -1
              check.price = check.orignalPrice * check.count
          }else if(newOrder['plus']){
              check.count += +1 
              check.price = check.orignalPrice * check.count
          }
          newOrder = {id : 'true'}
        }
        else if(check.id === newOrder.id){
          check.price += newOrder.price
          check.count += newOrder.count
          newOrder = {id : 'true'}
        }
        return 0 
      })
      newOrder.id.includes('true') ? setMyOrder([...myOrder]) : setMyOrder([...myOrder , newOrder])
    }
    useEffect(() => {
      const totalOrder = myOrder.length === 0 ? 0 : myOrder.map(cO => cO.count).reduce((a,b) => a+b)
      console.log('props')
      return props.myOrderCount(totalOrder)

    },[myOrder])


  return (
    <div>
        {props.cardOpen && <CardValue onsetMyOrder ={onsetMyOrder} myOrder = {myOrder} onCloseCard={props.onCloseCard}/>}
        <FloatMessage />
        <MealsIteam onsetMyOrder ={onsetMyOrder}/>
    </div>
  )
}

export default AppBody
