import React , { useEffect, useState } from 'react'
import MealItemForm from './MealItemForm';
const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
  },
  {
    id: 'm3',
    name: 'Barbecue Burger',
    description: 'American, raw, meaty',
    price: 12.99,
  },
  {
    id: 'm4',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
  },
];
function MealsIteam(props) {
   const [dummyMeals] =  useState(DUMMY_MEALS)
  const getData = async () => {
    const res= await fetch('https://aku-310-default-rtdb.firebaseio.com/Food.json');
    const data = await res.json()
    const foodList = [];
    for (const key in data) {
      foodList.push({
        id : key,
        name : key.name,
        description : key.description,
        price : key.price
      })
    }
    console.log(data)
  }
  useEffect(() => {
      getData();
  },[])

  // console.log('MealsIteam.jsx')

  return (
    <div className='container'> 
        <ul>
             {dummyMeals.map((avaMeal) => {
             return (
            <li key={avaMeal.id} className='meal'>
               <div >
                    <h3>{avaMeal.name}</h3>
                    <div className='description'>{avaMeal.description}</div>
                    <div className='price'>{`₹ ${avaMeal.price.toFixed(2)}`}</div>
               </div>
               <div>
                    <MealItemForm avaMeal = {avaMeal}  />
               </div>
            </li>
            );
            })}
        </ul>
    </div>
  )
}

export default React.memo(MealsIteam)
