import React from 'react'
const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
  },

];
const [count, setCount] = useState(0)

    const onsubtract = () => {
        if(count > 0){
            setCount(count - 1)
        }
        }
    const onsAdding = () => {
        if(count < 5){
            setCount(count + 1)
        }
        }
const Total = DUMMY_MEALS.map(a => a.price)
function CardValue() {
  return (
   <div className='popup card-value'>
                <ul>
             {DUMMY_MEALS.map((avaMeal) => {
             return (
            <li key={avaMeal.id} className='meal'>
               <div >
                    <h3>{avaMeal.name}</h3>
                    <div className='price'>{`₹ ${avaMeal.price.toFixed(2)}`}</div>
               </div>
               <button type='button' className='btn btn-outline-success' onClick={onsubtract}><b>-</b></button>
            <b className='m-3'>{count}</b>
            <button type='button' className='btn btn-outline-success'  onClick={onsAdding}><b>+</b></button> 

            </li>
            );
            })}
            <li className='meal'>
                 <h3>Total</h3>
                 <div className='price'>{`₹ ${Total.reduce((a,b) => a+b).toFixed(2)}`}</div>
            </li>
        </ul>
      </div>
  )
}

export default CardValue
