import React, { useState } from 'react'

function MealItemForm() {
   const [count, setCount] = useState(0)
    const onSubmitHandler = (event) => {
        event.preventDefault()
    }
    const onsubtract = () => {
        if(count > 0){
            setCount(count - 1)
        }
        }

  return (
        <form onSubmit={onSubmitHandler}>
            <button className='btn btn-outline-success' onClick={onsubtract}><b>-</b></button>
            <b className='m-3'>{count}</b>
            <button className='btn btn-outline-success'  onClick={() => setCount(count + 1)}><b>+</b></button> 
            <div>
                <button className='btn btn-outline-success mx-2 my px-4' > + Add</button>
            </div>
        </form>
  )
}

export default MealItemForm
