import React from 'react'
const DUMMY_MEALS = [
  {
    id: 'm1',
    name: 'Sushi',
    description: 'Finest fish and veggies',
    price: 22.99,
  },
  {
    id: 'm2',
    name: 'Schnitzel',
    description: 'A german specialty!',
    price: 16.5,
  },
  {
    id: 'm3',
    name: 'Barbecue Burger',
    description: 'American, raw, meaty',
    price: 12.99,
  },
  {
    id: 'm4',
    name: 'Green Bowl',
    description: 'Healthy...and green...',
    price: 18.99,
  },
];
function CardValue() {
  return (
   <div className='popup card-value'>
                <ul>
             {DUMMY_MEALS.map((avaMeal) => {
             return (
            <li key={avaMeal.id} className='meal'>
               <div >
                    <h3>{avaMeal.name}</h3>
                    <div className='description'>{avaMeal.description}</div>
                    <div className='price'>{`₹ ${avaMeal.price.toFixed(2)}`}</div>
               </div>
            </li>
            );
            })}
        </ul>
      </div>
  )
}

export default CardValue
